mapper.update:
UPDATE MEMBER_TBL_02 
SET	phone=#{phone}, address=#{address}, city=#{city} 
WHERE custno = #{custNo}

mapper.select:
 	select * from member_tbl_02 
	 	
	 		where custname like '%'||#{find}||'%'
	 	
	 	
	 		where address like '%'||#{find}||'%'
	 	
	 	
	 		where city=#{find}
	 	
	 	
	 		where grade=#{find}
